import React from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import { useParams } from "react-router-dom";
import logements from "../../data/logements.json"
import Details from "../../components/Details";
import Tag from "../../components/Tag";
import Stars from "../../components/Stars";
import "../../styles/Accomodations.scss"

export default  function Accomodations() {
  const  { imageid } = useParams();
  const selectedAccomodation = logements.find((logement) => logement.id === imageid);
  const {title,location,host,tags, rating} = selectedAccomodation;
  const range = [1, 2, 3, 4, 5];

  return (
    <div>
      <Header />
      <Details titre={title} location={location} name={host.name} src={host.picture}/>
      <div className="ranking">
        <div className="ranking__tag-box">
          {tags.map ((tag,index) => 
          <Tag  key={`${tag}-${index}`} tag={tag}/>)}
        </div>
        <div className="ranking__stars-box">
          {range.map((rangeElem, index) => rating >= rangeElem? <Stars key={`${rangeElem}-${index}`} icon="fa-solid fa-star"/> : <Stars key={`${rangeElem}-${index}`} icon="fa-regular fa-star"/>)}
        </div>
      </div>
      <Footer />
    </div>  
  );
}
