import React from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import { useParams } from "react-router-dom";
import logements from "../../data/logements.json"
import Details from "../../components/Details";

export default  function Accomodations() {
  const  { imageid } = useParams();
  const selectedAccomodation = logements.find((logement) => logement.id === imageid);
  const {title, id, location,host} = selectedAccomodation;
  const selectedHostPict = logements.find((logement) => host.picture === imageid);
  const {hostPict} = selectedHostPict

  return (
    <div>
      <Header />
      <Details titre={title} location={location} name={host.name} src={hostPict} />
      <Footer />
    </div>  
  );
}
